let utils = {
	/**
	 * 根据百分比获取宽度
	 * 
	 * @param {*} percent 百分比
	 * @return 宽度
	 */
	getWidth(percent) {
		return Math.round(device.width * (percent / 100));
	},

	/**
	 * 根据百分比获取高度
	 * 
	 * @param {*} percent 百分比
	 * @return 高度
	 */
	getHeight(percent) {
		return Math.round(device.height * (percent / 100));
	},

	/**
	 * 进行点击
	 * 
	 * @param {*} widthPrecent 宽度百分比
	 * @param {*} heightPrecent 高度百分比
	 */
	onClick(widthPrecent, heightPercent) {
		// 获取高度和宽度像素
		let width = this.getWidth(widthPrecent);
		let height = this.getHeight(heightPercent);
		click(width, height);
	},

	/**
	 * 取色
	 * 
	 * @param {*} widthPrecent 宽度百分比
	 * @param {*} heightPrecent 高度百分比
	 */
	getColor(widthPrecent, heightPrecent) {
		// 获取高度和宽度像素
		let width = this.getWidth(widthPrecent);
		let height = this.getHeight(heightPrecent);

		// 进行截图取色
		let img = captureScreen();
		let color = images.pixel(img, width, height);
		return colors.toString(color);
	},

	/**
	 * 是否是结束页面
	 * 
	 * @param img 截图
	 */
	hasEnd(img) {
		// 宽度和高度百分比
		let widthPercent = 50;
		let heightPercent = 87;

		// 遍历高度
		for (let j = heightPercent - 15; j < heightPercent + 15 && j < 100; j += 0.5) {
			// 取出图片中指定位置的颜色
			let colorHex = colors.toString(images.pixel(img, this.getWidth(widthPercent), this.getHeight(j)));
			if (colorHex.substr(3, 2) === '44') {
				return true;
			}
		}
		return false;
	},

	/**
	 * 是否有逛一逛
	 * 
	 * @param img 截图
	 */
	hasWalk(img) {
		// 宽度和高度百分比
		let widthPercent = 90;
		let heightPercent = 68;

		// 获取高度和宽度像素
		let widths = [];
		let heights = [];

		// 初始化高度
		for (let i = heightPercent - 15; i < heightPercent + 15; i += 0.5) {
			heights.push(this.getHeight(i));
		}

		// 初始化宽度
		for (let i = widthPercent - 15; i < widthPercent + 15; i += 0.5) {
			if (this.getWidth(i) < device.width) {
				widths.push(this.getWidth(i));
			}
		}

		for (let width of widths) {
			for (let height of heights) {
				// 取出图片中指定位置的颜色
				let colorHex = colors.toString(images.pixel(img, width, height));
                console.log('[ ]',)
				// 如果取到逛一逛的颜色
				if (colorHex.substr(3, 2) === 'ff' && colorHex.substr(7, 2) === '00') {
					return {
						width: width,
						height: height
					};
				}
			}
		}
		return null;
	}
};

let action = {
	/**
	 * 初始化
	 */
	init() {
		// 请求截图
		if (!requestScreenCapture()) {
			toast("请求截图失败");
			exit();
		}
	},

	/**
	 * 逛一逛
	 */
	walk() {
		click(utils.getWidth(80), utils.getHeight(68));
		sleep(2000);
	},

	/**
	 * 进行偷能量
	 */
	steal() {
		// 要点击的坐标
		let coordinates = [{
			x: 21,
			y: 34
		}, {
			x: 23,
			y: 33
		}, {
			x: 33.3,
			y: 30
		}, {
			x: 37,
			y: 28
		}, {
			x: 45,
			y: 27
		}, {
			x: 50,
			y: 26
		}, {
			x: 66,
			y: 30
		}, {
			x: 72,
			y: 32
		}, {
			x: 80,
			y: 33
		}]

		for (let i = 0; i < 3; i++) {
			// 进行点击
			for (let coordinate of coordinates) {
				utils.onClick(coordinate.x, coordinate.y);
			}
			coordinates = coordinates.reverse();

			// 如果弹出装扮，截图，判断是否是帮好友复活能量
			let img  = captureScreen();
			let hex = colors.toString(images.pixel(img, utils.getWidth(50), utils.getHeight(50)));

			// 如果是帮好友复活能量，就点上面一点
			if (hex === '#ffffffff') {
				utils.onClick(50, 73);
				utils.onClick(50, 78);
			}
			
			// 如果是装扮，就点下面一点
			else {
				utils.onClick(50, 80);
				utils.onClick(50, 83);
			}
			sleep(1000);
		}
	}
};

// 初始化
action.init();

// 打开支付宝
launchApp(getAppName("com.eg.android.AlipayGphone"));
sleep(2000);

// 偷能量
while (true) {
	// 进行截图
	let img = captureScreen();

	// 判断是否是鸟叫页面
	if (utils.hasEnd(img)) {
		alert('偷能量结束');
		exit();
	}
	
	// 获取逛一逛坐标
	let coordinate = utils.hasWalk(img);

	// 判断是否有逛一逛图标
	if (coordinate !== null) {
		// 偷能量
		action.steal();
		// 点击逛一逛
		click(coordinate.width, coordinate.height);
		sleep(2000);
	} else {
		alert('获取逛一逛坐标失败');
		exit();
	}
}

